/*
 * [103] Binary Tree Zigzag Level Order Traversal
 *
 * https://leetcode.com/problems/binary-tree-zigzag-level-order-traversal
 *
 * Medium (31.83%)
 * Total Accepted:    81382
 * Total Submissions: 255663
 * Testcase Example:  '[3,9,20,null,null,15,7]'
 *
 * Given a binary tree, return the zigzag level order traversal of its nodes'
 * values. (ie, from left to right, then right to left for the next level and
 * alternate between).
 * 
 * 
 * For example:
 * Given binary tree [3,9,20,null,null,15,7],
 * 
 * 3
 * / \
 * 9  20
 * /  \
 * 15   7
 * 
 * 
 * 
 * return its zigzag level order traversal as:
 * 
 * [
 * [3],
 * [20,9],
 * [15,7]
 * ]
 * 
 * 
 */
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    vector<vector<int>> zigzagLevelOrder(TreeNode* root) {
        vector<vector<int>> vv;
        queue<TreeNode*> q;
        TreeNode *p, *last=NULL, *next_last=NULL;
        if (root!=NULL) {
            q.push(root);
            last = root;
            vector<int> v;
            vv.push_back(v);
        }
        while (!q.empty()) {
            p=q.front(); q.pop();
            vv.back().push_back(p->val);
            if (p->left!=NULL) { q.push(p->left); next_last=p->left; }
            if (p->right!=NULL) { q.push(p->right); next_last=p->right; }
            if (p==last) {
                if (vv.size() % 2==0) reverse(vv.back().begin(), vv.back().end());
                if (next_last!=NULL) {
                    vector<int> v; vv.push_back(v);
                }
                last=next_last; next_last=NULL;
            }
        }
        return vv;
    }
};
