/*
 * [105] Construct Binary Tree from Preorder and Inorder Traversal
 *
 * https://leetcode.com/problems/construct-binary-tree-from-preorder-and-inorder-traversal
 *
 * Medium (31.09%)
 * Total Accepted:    91271
 * Total Submissions: 293577
 * Testcase Example:  '[]\n[]'
 *
 * Given preorder and inorder traversal of a tree, construct the binary tree.
 * 
 * Note:
 * You may assume that duplicates do not exist in the tree.
 * 
 */
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    TreeNode* buildTree(vector<int>& pre, vector<int>& in) {
        return buildTree(&pre[0], &pre[0]+pre.size(), &in[0], &in[0]+in.size());
    }
    
    TreeNode* buildTree(int* pre_b, int* pre_e, int* in_b, int* in_e) {
        if (pre_b == pre_e) return NULL;
        TreeNode* p = new TreeNode(*pre_b);
        int i = 0;
        while (in_b+i != in_e) {
            if (in_b[i]==*pre_b) break;
            ++i;
        }
        p->left = buildTree(pre_b+1, pre_b+1+i, in_b, in_b+i);
        p->right = buildTree(pre_b+1+i, pre_e, in_b+i+1, in_e);
        return p;
    }
};
