/*
 * [108] Convert Sorted Array to Binary Search Tree
 *
 * https://leetcode.com/problems/convert-sorted-array-to-binary-search-tree
 *
 * Easy (40.89%)
 * Total Accepted:    109611
 * Total Submissions: 268020
 * Testcase Example:  '[]'
 *
 * Given an array where elements are sorted in ascending order, convert it to a
 * height balanced BST.
 */
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    TreeNode* sortedArrayToBST(vector<int>& nums) {
        return sortedArrayToBST(&nums[0], &nums[0]+nums.size());
    }
    
    TreeNode* sortedArrayToBST(int *b, int *e) {
        if (b==e) return NULL;
        int n = e-b, x = n/2;
        TreeNode *p = new TreeNode(*(b+x));
        p->left = sortedArrayToBST(b,b+x);
        p->right = sortedArrayToBST(b+x+1, e);
        return p;
    }
};
