/*
 * [12] Integer to Roman
 *
 * https://leetcode.com/problems/integer-to-roman
 *
 * Medium (43.32%)
 * Total Accepted:    95349
 * Total Submissions: 220086
 * Testcase Example:  '1'
 *
 * Given an integer, convert it to a roman numeral.
 * 
 * 
 * Input is guaranteed to be within the range from 1 to 3999.
 */
class Solution {
public:
    string intToRoman(int num) {
        ostringstream oss;
        while (num>=1000) { oss << "M"; num-=1000; }
        if (num>=900) { oss << "CM"; num-=900; }
        while (num>=500) { oss << "D"; num-=500; }
        while (num>=400) { oss << "CD"; num-=400; }
        while (num>=100) { oss << "C"; num-=100; }
        if (num>=90) { oss << "XC"; num-=90; }
        while (num>=50) { oss << "L"; num-=50; }
        while (num>=40) { oss << "XL"; num-=40; }
        while (num>=10) { oss << "X"; num-=10; }
        if (num>=9) { oss << "IX"; num-=9; }
        while (num>=5) { oss << "V"; num-=5; }
        while (num>=4) { oss << "IV"; num-=4; }
        while (num>=1) { oss << "I"; num-=1; }

        return oss.str();
    }
};
