/*
 * [150] Evaluate Reverse Polish Notation
 *
 * https://leetcode.com/problems/evaluate-reverse-polish-notation
 *
 * Medium (26.24%)
 * Total Accepted:    86081
 * Total Submissions: 328021
 * Testcase Example:  '["18"]'
 *
 * 
 * Evaluate the value of an arithmetic expression in Reverse Polish
 * Notation.
 * 
 * 
 * 
 * Valid operators are +, -, *, /. Each operand may be an integer or another
 * expression.
 * 
 * 
 * 
 * Some examples:
 * 
 * ["2", "1", "+", "3", "*"] -> ((2 + 1) * 3) -> 9
 * ["4", "13", "5", "/", "+"] -> (4 + (13 / 5)) -> 6
 * 
 * 
 */
class Solution {
public:
    int get(stack<int> &nums) {
        int x = nums.top();
        nums.pop();
        return x;
    }
    int evalRPN(vector<string>& tokens) {
        stack<int> nums; int v;
        for (auto s : tokens) {
            if (s!="+" && s!="-" && s!="*" && s!="/") {
                v=stoi(s);    
            } else {
                int v2=get(nums), v1=get(nums);
                if (s=="+") v=v1+v2;
                else if (s=="-") v=v1-v2;
                else if (s=="*") v=v1*v2;
                else if (s=="/") v=v1/v2;
            }
            nums.push(v);
        }
        return nums.top();
    }
};
