/*
 * [228] Summary Ranges
 *
 * https://leetcode.com/problems/summary-ranges
 *
 * Medium (28.58%)
 * Total Accepted:    71588
 * Total Submissions: 250424
 * Testcase Example:  '[]'
 *
 * 
 * Given a sorted integer array without duplicates, return the summary of its
 * ranges.
 * 
 * 
 * For example, given [0,1,2,4,5,7], return ["0->2","4->5","7"].
 * 
 * 
 * Credits:Special thanks to @jianchao.li.fighter for adding this problem and
 * creating all test cases.
 */
class Solution {
public:
    vector<string> summaryRanges(vector<int>& nums) {
        vector<string> ranges;
        if (nums.empty()) return ranges;
        
        int last = 0, n = nums.size();
        for (int i = 1; i<n; ++i) {
            if (nums[i]==nums[i-1] || nums[i]==nums[i-1]+1) {
                continue;
            } else {
                if (i-1==last) ranges.push_back(to_string(nums[last]));
                else ranges.push_back(to_string(nums[last])+"->"+to_string(nums[i-1]));
                last = i;
            }
        }
        if (last==n-1) ranges.push_back(to_string(nums[last]));
        else ranges.push_back(to_string(nums[last])+"->"+to_string(nums[n-1]));
        return ranges;
    }
};
