/*
 * [275] H-Index II
 *
 * https://leetcode.com/problems/h-index-ii
 *
 * Medium (33.75%)
 * Total Accepted:    42402
 * Total Submissions: 125623
 * Testcase Example:  '[]'
 *
 * 
 * Follow up for H-Index: What if the citations array is sorted in ascending
 * order? Could you optimize your algorithm?
 * 
 * 
 * 
 * Expected runtime complexity is in O(log n) and the input is sorted.
 * 
 */
class Solution {
public:
    int hIndex(vector<int>& c) {
        int n=c.size(), i=0, j=n-1;
        if (n==0) return 0;
        while (i<=j) {
            int m = (i+j)/2;
            if (c[m]>=n-m) j=m-1;
            else i=m+1;
        }
        if (i<n && c[i]>=n-i) return n-i;
        if (i>=n || c[i]<n-i ) return 0;
    }
};
