#
# [54] Spiral Matrix
#
# https://leetcode.com/problems/spiral-matrix
#
# Medium (24.93%)
# Total Accepted:    89818
# Total Submissions: 360202
# Testcase Example:  '[[1,2,3],[4,5,6],[7,8,9]]'
#
# Given a matrix of m x n elements (m rows, n columns), return all elements of
# the matrix in spiral order.
# 
# 
# 
# For example,
# Given the following matrix:
# 
# 
# [
# [ 1, 2, 3 ],
# [ 4, 5, 6 ],
# [ 7, 8, 9 ]
# ]
# 
# 
# You should return [1,2,3,6,9,8,7,4,5].
# 
#
class Solution:
    def spiralOrder(self, vv):
        v=[]
        n=len(vv)
        if n==0: return v
        m=len(vv[0])
        if m==0: return v
        
        i_l = j_l = 0
        i_h = n-1
        j_h = m-1
        i = j = 0
        while len(v)<=n*m:
            while j <= j_h:
                v.append(vv[i][j])
                if len(v)>=n*m: return v
                j+=1
            i_l+=1
            j-=1
            i+=1
            while i <= i_h:
                v.append(vv[i][j])
                if len(v)>=n*m: return v
                i+=1
            j_h-=1
            i-=1
            j-=1
            while j >= j_l:
                v.append(vv[i][j])
                if len(v)>=n*m: return v
                j-=1
            i_h-=1
            j+=1
            i-=1
            while i >= i_l:
                v.append(vv[i][j])
                if len(v)>=n*m: return v
                i-=1
            j_l+=1
            i+=1
            j+=1
        return v
