/*
 * [97] Interleaving String
 *
 * https://leetcode.com/problems/interleaving-string
 *
 * Hard (24.10%)
 * Total Accepted:    65025
 * Total Submissions: 269871
 * Testcase Example:  '""\n""\n""'
 *
 * 
 * Given s1, s2, s3, find whether s3 is formed by the interleaving of s1 and
 * s2.
 * 
 * 
 * 
 * For example,
 * Given:
 * s1 = "aabcc",
 * s2 = "dbbca",
 * 
 * 
 * When s3 = "aadbbcbcac", return true.
 * When s3 = "aadbbbaccc", return false.
 * 
 */
class Solution {
public:
    bool isInterleave(string s1, string s2, string s3) {
        int n1=s1.size(),n2=s2.size(),n3=s3.size();
        if (n1==0) return s2==s3;
        if (n2==0) return s1==s3;
        if (n1+n2 != n3) return false;

        vector<vector<int> > vv(n1+1);
        for (int i=0;i<=n1;++i) vv[i].resize(n2+1);
        for (int i=0;i<=n1;++i) {
            for (int j=0;j<=n2;++j) {
                bool x = (i==0 && j==0);
                if (i>0) x = x || (vv[i-1][j] && s1[i-1]==s3[i+j-1]);
                if (j>0) x = x || (vv[i][j-1] && s2[j-1]==s3[i+j-1]);
                vv[i][j] = x;
            }
        }

        return vv[n1][n2];
    }
};
